import React from 'react'
import { Box, Card, FormControl, FormLabel, Heading, Input, Text, VStack, Button, CardBody, Image } from "@chakra-ui/react";

export const Register = () => {
    const [show, setShow] = useState(false)
    const handleClick = () => setShow(!show)


    const inputUsernameOrEmail = useRef()
    const inputPassword = useRef()

    let onLogin = async () => {
        try {
            let usernameOrEmail = inputUsernameOrEmail.current.value
            let password = inputPassword.current.value

            let response = await axios.get(`http://localhost:8000/users/login?usernameOrEmail=${usernameOrEmail}&password=${password}`)
            toast(response.data.message)
            localStorage.setItem('myToken', response.data.data.token)
        } catch (error) {
            toast(error.response.data.message)
        }
    }

    return (
        <Box justifyContent={"center"} display={'flex'} flexDirection={'column'} alignItems={'center'} gap={6} >
            {/* <Image src={HeaderPattern}></Image> */}
            <VStack as='header' spacing='6' mt='181'>
                <Heading as='h1' fontWeight='600' fontSize='36px' letterSpacing='0,5px'>
                    What's Your Name?
                </Heading>
            </VStack>
            <Card maxWidth='300px'>
                <CardBody>
                    <FormControl>
                        <FormLabel size='sm'>
                            First Name
                        </FormLabel>
                        <Input type='text' bg='white' borderColor='#d8dee4' size='sm' borderRadius='6px' />
                    </FormControl>
                    <FormControl>
                        <FormLabel size='sm'>
                            Last Name
                        </FormLabel>
                        <Input type='text' bg='white' borderColor='#d8dee4' size='sm' borderRadius='6px' />
                    </FormControl>
                    <Button rounded='3xl' w="248px" h="40px" alignSelf='center' backgroundColor='#5D5FEF' color='white' mt='5' >
                        <Text className=" font-ibmFontRegular" >
                            NEXT
                        </Text>
                    </Button>
                </CardBody>
            </Card>
        </Box>

    )
}
